// Load and process oracle prompt
const inputData = $input.first().json;

// FIX: This is the corrected way to access the binary data.
// $input.first() gets the single item from the Oracle Prompt Loader node.
// .binary then gives you access to the binary data object.
const binaryData = $input.first().binary;
console.log(binaryData);

try {
  // Read the oracle prompt file.
  // The key for the binary file is often the filename. We can get it dynamically.
  const binaryKey = Object.keys(binaryData)[0];
  const oracleFile = binaryData[binaryKey];

  // This line is now fixed because 'oracleFile.data' correctly points to the binary data.
  const oracleContent = Buffer.from(oracleFile.data, 'base64').toString('utf8');
  const oraclePrompt = JSON.parse(oracleContent);

  // Build the prompt based on oracle template
  let finalPrompt;

  if (inputData.prompt_type === 'corporate_climber') {
    finalPrompt = `Create a professional podcast interview prep package for:

Name: ${inputData.guest_name}
Email: ${inputData.guest_email}
Profile: ${inputData.profile_text}
Role: ${inputData.job_description}

Generate a CORPORATE CLIMBER style interview package focusing on:
- Executive leadership development
- Strategic decision-making and business impact
- Team management and organizational growth
- Boardroom presence and corporate excellence

Return ONLY valid JSON in this exact format:
{
  "episode_title": "Executive leadership focused title",
  "guest_bio": "Professional biography emphasizing leadership credentials",
  "leadership_story_1": "Key leadership achievement with business impact",
  "leadership_story_2": "Strategic decision that drove results",
  "team_impact_story": "Team development or organizational transformation",
  "question_1": "Strategic leadership and vision question",
  "question_2": "Team management and people development question",
  "question_3": "Business strategy and market positioning question",
  "professional_bio": "Extended executive summary with quantified achievements",
  "talking_points": "Key discussion points for executive-level interview"
}

Focus on leadership impact, strategic thinking, business results, and executive presence.`;
  } else {
    // Default prompt
    finalPrompt = `Create a professional podcast episode package for:

Name: ${inputData.guest_name}
Profile: ${inputData.profile_text}
Role: ${inputData.job_description}

Return valid JSON only:
{
"episode_title": "title",
"guest_bio": "bio",
"industry_focus": "industry",
"win_story_1": "story 1",
"win_story_2": "story 2",
"question_1": "question?",
"question_2": "question?", 
"question_3": "question?",
"key_insights": ["insight1", "insight2", "insight3"],
"episode_summary": "summary"
}`;
  }

  // Return combined data
  return [{
    json: {
      ...inputData,
      oracle_prompt: oraclePrompt,
      final_prompt: finalPrompt,
      oracle_loaded: true
    }
  }];

} catch (error) {
  // Fallback if oracle file not found or parsing failed
  const fallbackPrompt = `Create a professional podcast episode package for:

Name: ${inputData.guest_name}
Profile: ${inputData.profile_text}
Role: ${inputData.job_description}

Return valid JSON only:
{
"episode_title": "title",
"guest_bio": "bio",
"industry_focus": "industry",
"win_story_1": "story 1",
"win_story_2": "story 2",
"question_1": "question?",
"question_2": "question?", 
"question_3": "question?",
"key_insights": ["insight1", "insight2", "insight3"],
"episode_summary": "summary"
}`;

  return [{
    json: {
      ...inputData,
      final_prompt: fallbackPrompt,
      oracle_loaded: false,
      oracle_error: error.message
    }
  }];
}

