const webhook = $('üéØ Webhook Input').first().json.body;

const guestName = webhook.guest_name || "Unknown Guest";
const guestEmail = webhook.guest_email || "Unknown Email";
const profileText = webhook.profile_text || "No profile provided";
const jobDescription = webhook.job_description || "No job description provided";

// Build the AI prompt
const aiPrompt = `
Create a professional podcast interview prep package for:

Name: ${guestName}

Email: ${guestEmail}

Profile: ${profileText}

Role: ${jobDescription}

Generate a GENERAL PROFESSIONAL interview package focusing on:
- Professional background and expertise
- Career achievements and milestones
- Industry insights and experience
- Personal brand and unique value proposition
- also re-make anf summarize into a sensible version of the role into a $input.first().json.body.prompt_type job descrpition suitable for $input.first().json.body.profile_text 

Return ONLY valid JSON in this exact format:
{
  "episode_title": "Professional focused title",
  "guest_bio": "Professional biography highlighting expertise",
  "win_story_1": "Key professional achievement",
  "win_story_2": "Career milestone or success story", 
  "industry_insight": "Valuable industry perspective or trend",
  "question_1": "Professional background and expertise question",
  "question_2": "Career journey and achievements question",
  "question_3": "Industry insights and future outlook question",
  "professional_bio": "Extended professional summary with key accomplishments",
  "talking_points": "Key discussion points for professional interview"
}

Focus on professional expertise, career achievements, industry knowledge, and personal brand.

Here is the Job Description:
---START JOB DESCRIPTION---
${jobDescription}
---END JOB DESCRIPTION---

Here is the Candidate's CV:
---START CANDIDATE CV---
${profileText}
---END CANDIDATE CV---
`;

return {
  json: {
    aiPrompt
  }
};

const rawOutput = items[0].json.output;

// This regular expression finds the content inside a markdown code block.
const jsonContentMatch = rawOutput.match(/```json\n(.*)\n```/s);

// Check if a match was found and the content is not empty.
if (jsonContentMatch && jsonContentMatch[1]) {
  try {
    // Parse the extracted string into a JavaScript object.
    const parsedJson = JSON.parse(jsonContentMatch[1]);
    // Return the parsed JSON object for the next node to use.
    return [{ json: parsedJson }];
  } catch (error) {
    // If parsing fails, return an error message to the next node.
    console.error("Failed to parse JSON content from AI response:", error);
    return [{ json: { error: "Failed to parse JSON content from AI response", originalContent: rawOutput } }];
  }
} else {
  // If the markdown fences are not found, the entire output might be the JSON string.
  try {
    const parsedJson = JSON.parse(rawOutput);
    return [{ json: parsedJson }];
  } catch (error) {
    console.error("JSON content not found or is invalid:", error);
    return [{ json: { error: "JSON content not found or is invalid", originalContent: rawOutput } }];
  }
}
