// Load input JSON data from the previous node.
const inputJson = $input.first().json;

// Helper to extract binary content
function getBinaryContent(binaryData, key) {
  if (binaryData && binaryData[key] && binaryData[key].data) {
    return Buffer.from(binaryData[key].data, 'base64').toString('utf8');
  }
  return '';
}

// Grab binary files (profile + sales brief)
const binaryData = $input.first().binary;
const profileContent = getBinaryContent(binaryData, 'profile_file');
const briefContent = getBinaryContent(binaryData, 'brief_file');

let systemPrompt, userPrompt;

// If prompt_type is "sales_pitch", build a sales/discovery style package
if (inputJson.prompt_type === 'sales_pitch') {

  systemPrompt = `You are a professional sales enablement package generator.
Your job is to analyze the provided client profile and sales brief to generate a
compelling discovery + pitch framework that helps a salesperson excite the client
about automation tools and their benefits.

Your response must be ONLY a valid JSON object. No extra text, no explanations.
The JSON must strictly follow this format:

{
  "pitch_title": "Attention-grabbing sales pitch title",
  "client_bio": "Concise summary of the client background",
  "pain_points": ["pain point 1", "pain point 2", "pain point 3"],
  "opportunity_story": "Narrative showing how automation creates value",
  "case_study_example": "Relevant example showing proof of results",
  "discovery_questions": [
    "Open-ended discovery question 1",
    "Exploratory question 2",
    "Challenge-oriented question 3"
  ],
  "benefits_summary": "Key quantified benefits of automation",
  "next_steps": "Clear suggested call-to-action for the client"
}`;

  userPrompt = `Generate a SALES PITCH package for automation tools.

Client Name: ${inputJson.first_name || inputJson.client_name}
Email: ${inputJson.contact_email || "N/A"}

<profile_content>
${profileContent}
</profile_content>

<brief_content>
${briefContent}
</brief_content>

Focus on: identifying automation opportunities, showing ROI, and framing exciting
discovery questions that drive engagement.`;

} else {
  // Default fallback
  systemPrompt = "You are a structured copy generator. Always return JSON.";
  userPrompt = "Provide structured JSON output.";
}

// Return prompts
return [{
  json: {
    ...inputJson,
    system_prompt: systemPrompt,
    user_prompt: userPrompt,
    oracle_loaded: true
  }
}];

