      final_prompt: fallbackPrompt,
      oracle_loaded: false,
      oracle_error: error.message
    }
  }];
}

const aiOutput = items[0].json.output;
let webhookBody = {};

// Find the webhook body, it could be in a few places depending on the node configuration
if (items[0].json.body) {
  webhookBody = items[0].json.body;
} else if (items[0].json.parameters && items[0].json.parameters.body) {
  webhookBody = items[0].json.parameters.body;
} else {
  // If all else fails, assume the initial item contains the body directly
  webhookBody = items[0].json;
}

let jsonString = aiOutput;

// Check for and remove the JSON markdown fences if they exist
if (jsonString.startsWith('```json')) {
  jsonString = jsonString.replace(/^```json\s*|```$/g, '').trim();
}

try {
  // Parse the clean JSON string into a JavaScript object
  const parsedJson = JSON.parse(jsonString);

  // Function to recursively replace template variables in the object
  function replaceTemplateVars(obj) {
    for (const key in obj) {
      if (typeof obj[key] === 'string') {
        let value = obj[key];
        // Replace each N8N template variable with its value from the webhook body
        // We use a regex to ensure a global replacement
        value = value.replace(/{{ \$json\.body\.guest_name }}/g, webhookBody.guest_name);
        value = value.replace(/{{ \$json\.body\.guest_email }}/g, webhookBody.guest_email);
        value = value.replace(/{{ \$json\.body\.profile_text }}/g, webhookBody.profile_text);
        value = value.replace(/{{ \$json\.body\.jobDescription }}/g, webhookBody.jobDescription);
        obj[key] = value;
      } else if (typeof obj[key] === 'object' && obj[key] !== null) {
        replaceTemplateVars(obj[key]);
      }
    }
  }

  // Replace all the variables in the parsed JSON object
  replaceTemplateVars(parsedJson);

  // Return the final, clean JSON object for the next node
  return [{ json: parsedJson }];

} catch (error) {
  // If parsing fails, return an error message
  console.error("Failed to process JSON from AI:", error);
  return [{ json: { error: "Failed to process JSON from AI", originalContent: aiOutput } }];
}

